sys_script_fix.script:
var gr1 = new GlideRecord('core_company');
gr1.addNotNullQuery('country');
gr1.query();

while (gr1.next()) {

    var oldCountry = gr1.country;
    var gr2 = new GlideRecord('core_country');
    gr2.addQuery('name', oldCountry).addOrCondition('iso3166_3', oldCountry);
    gr2.query();

    while (gr2.next()) {
        gr1.u_country = gr2.sys_id;
        gr1.update();
    }
}
