sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        this.rightnow = new GlideDateTime();
        this.testdate = new GlideDateTime(strDate);
    },
    isDatePast: function() {
        // If the testdate is before rightnow, return true, else return false
        if (this.testdate.before(this.rightnow)) {
            return true;
        } else {
            return false;
        }
    },
    isDateToday: function() {
        // Challenge:  Do not allow same-day requests
        // Get the date portion of rightnow and whenNeeded (no timestamp)
        var today = this.rightnow.getLocalDate();
        var istoday = this.testdate.getLocalDate();

        // If the testdate is before rightnow, return true, else return false
        if (today.compareTo(istoday) == 0) {
            return true;
        } else {
            return false;
        }
    },
    type: 'NeedItUtils'
};
